import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{o as i,c as e,e as a}from"./app-a1fbcbb6.js";const r={},t=a('<h2 id="全局变量和局部变量" tabindex="-1"><a class="header-anchor" href="#全局变量和局部变量" aria-hidden="true">#</a> 全局变量和局部变量</h2><ul><li>变量作用域基本概念</li></ul><ul><li>变量作用域：变量的可用范围</li><li>按照作用域的不同，变量可以分为：局部变量和全局变量</li></ul><ul><li>局部变量</li></ul><ul><li>定义在函数内部的变量以及函数的形参, 我们称为局部变量</li><li>作用域：从定义的那一行开始, 直到遇到}结束或者遇到return为止</li><li>生命周期: 从程序运行到定义哪一行开始分配存储空间到程序离开该变量所在的作用域</li><li>存储位置: 局部变量会存储在内存的栈区中</li><li>特点：</li><li>相同作用域内不可以定义同名变量</li><li>不同作用范围可以定义同名变量，内部作用域的变量会覆盖外部作用域的变量</li></ul><ul><li>全局变量</li></ul><ul><li>定义在函数外面的变量称为全局变量</li><li>作用域范围：从定义哪行开始直到文件结尾</li><li>生命周期:程序一启动就会分配存储空间,直到程序结束</li><li>存储位置：静态存储区</li><li>特点: 多个同名的全局变量指向同一块存储空间</li></ul><h2 id="" tabindex="-1"><a class="header-anchor" href="#" aria-hidden="true">#</a></h2>',8),c=[t];function n(o,u){return i(),e("div",null,c)}const h=l(r,[["render",n],["__file","30.html.vue"]]);export{h as default};
